import React, {useState} from 'react';
import './App.css';
import Home from './components/home/Home';
import NavBar from './components/navbar/NavBar';
import About from './components/about/About';
import Login from './components/login/Login';
import Signup from './components/signup/Signup';
import Petlist from './components/petlist/PetList';
import Contact from './components/contact/Contact';
import { BrowserRouter, Route, Routes } from 'react-router-dom';


function App() {

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userId, setUserId] = useState(null);
  const [pets, setPets] = useState([]);
  const [error,seterror] = useState([])

  React.useEffect(() => {
    fetch(`https://zamil-petfinder.onrender.com/pet`)
      .then((response) => response.json())
      .then((data) => setPets(data.data))
      .catch((error) => seterror(error));
  }, []);

  function handleSearchName(search){
    setPets((pets)=>{
      return pets.filter((pet)=>{
        return pet.name.toLowerCase().includes(search)

      })
    })
  }

  function handleSearchBreed(search){
    setPets((pets)=>{
      return pets.filter((pet)=>{
        return pet.breed.toLowerCase().includes(search)

      })
    })
  }


  return (
    <div className="App">
      <BrowserRouter>
      <NavBar
      isLoggedIn={isLoggedIn}
      setIsLoggedIn={setIsLoggedIn}/>
      <Routes>
        <Route path="/home" element={<Home 
        handleSearchCat = {handleSearchBreed}
        handleSearchLoc = {handleSearchName}/>} />
        <Route path="/petlist" element={<Petlist
        userId={userId}
        pets = {console.loglog(pets)}
        setPets = {setPets}
        />} />
        <Route path="/about" element={<About />} />
        <Route path="/contact" element={<Contact />} />
        <Route path="/" element={<Login 
        userId={userId}
        setUserId={setUserId}
        isLoggedIn={isLoggedIn}
        setIsLoggedIn={setIsLoggedIn}/>} />
        <Route path="/signup" element={<Signup />} />
      </Routes>
      </BrowserRouter>

    </div>
  );
}

export default App;
